INSERT INTO BOOK_CATALOG(ID_book,NAME,AUTHOR,GENRE,YEAR,COST,DESCRIPTION) VALUES  (11,'Предания языческой старины западных славян ','Громанн Й. В. ','эпос и фольклор','2017','407',' Широкому кругу читателей предания, имеющие корни архаичные, уходящие в языческие времена, будут интересны в первую очередь в сравнении с аналогичными русскими народными быличками о домовых, водяных и леших, преданиями о призраках в замках, легендами о зачарованных кладах. За чешким колоритом просматривается общеславянская дохристианская традиция, описывающая таинственный мир духов природы и древних Богов.');
INSERT INTO BOOK_CATALOG(ID_book,NAME,AUTHOR,GENRE,YEAR,COST,DESCRIPTION) VALUES  (12,'Лебединый стан ','Цветаева Марина Ивановна ','поэзия','2017','624','Книга издана к 125-летию поэтессы и адресована всем поклонникам творчества Марины Цветаевой, ценителям отечественной поэтической культуры Серебряного века.');
