INSERT INTO BOOK_CATALOG(ID_book,NAME,AUTHOR,GENRE,YEAR,COST,DESCRIPTION) VALUES  (16,'Портрет Дориана Грея ','Уайльд Оскар ','зарубежная классика','2017','206','"Портрет Дориана Грея" - самое знаменитое произведение Оскара Уайльда, единственный его роман, вызвавший в свое время шквал негативных оценок и тем не менее имевший невероятный успех.');
INSERT INTO BOOK_CATALOG(ID_book,NAME,AUTHOR,GENRE,YEAR,COST,DESCRIPTION) VALUES  (17,'Дым и зеркала ','Гейман Нил ','современная проза','2017','459','В сборнике "Дым и зеркала" воображение и артистизм мастера превращают обычный мир в место, где возможны самые нелепые и странные вещи: здесь старушка покупает Святой Грааль в секонд-хенде, американский турист попадает в лавкрафтовский городок Инсмут, где живут приспешники Великого Ктулху, наемные убийцы предлагают свои услуги в "Желтых страницах", а оборотень пытается воспрепятствовать концу света.');
INSERT INTO BOOK_CATALOG(ID_book,NAME,AUTHOR,GENRE,YEAR,COST,DESCRIPTION) VALUES  (18,'Мартин Иден ','Лондон Джек ','роман','2017','230','"Мартин Иден", выдающийся роман Джека Лондона о человеке из низов, добившемся успеха, о поиске места в жизни, муках творчества и муках любви, оказался пророческим: Джек Лондон во многом повторил судьбу своего героя.');
