INSERT INTO BOOK_CATALOG(ID_book,NAME,AUTHOR,GENRE,YEAR,COST,DESCRIPTION) VALUES  (28,'Ведьмак. Сезон гроз ','Сапковский Анджей ','фэнтези','2013','718','Сезон гроз (польск. Sezon burz) — роман в жанре фэнтези польского писателя-фантаста Анджея Сапковского, восьмая книга в серии «Ведьмак» о приключениях Геральта из Ривии. ');
INSERT INTO BOOK_CATALOG(ID_book,NAME,AUTHOR,GENRE,YEAR,COST,DESCRIPTION) VALUES  (29,'Позвони мне, позвони ','Рождественский Роберт Иванович ','поэзия','2017','438','"Я знала, что многие нам завидуют, еще бы - столько лет вместе. Но если бы они знали, как мы счастливы, нас, наверное, сожгли бы на площади. Каждый день я слышала: "Алка, я тебя люблю!" Я так привыкла к этим словам, что не могу поверить, что никогда (какое слово бесповоротное!) не услышу их снова. Но они звучат в ночи, заставляют меня просыпаться и не оставляют никакой надежды на сон...", - такими словами супруга поэта Алла Киреева предварила настоящий сборник стихов.');
INSERT INTO BOOK_CATALOG(ID_book,NAME,AUTHOR,GENRE,YEAR,COST,DESCRIPTION) VALUES  (30,'Голливуд ','Буковски Чарльз ','роман','1989','436','В основе романа «Голливуд» лежит реальная история работы Буковски над сценарием фильма «Пьянь», который был поставлен режиссером Барбетом Шредером в 1987 году.');
INSERT INTO BOOK_CATALOG(ID_book,NAME,AUTHOR,GENRE,YEAR,COST,DESCRIPTION) VALUES  (31,'Проклятие королей ','Грегори Филиппа ','роман','2014','356','Ее зовут Маргарет Поул. Племянницу двух королей, дочь самой богатой женщины Англии и сестру человека, который должен был взойти на престол, теперь отлучили от двора и спрятали в глуши. Но забвением и скромным поместьем не напугать Маргарет. Ей, потерявшей всю семью, уже нечего терять, но есть за что бороться. И Маргарет сделает все, чтобы восстановить справедливость и вернуть себе прежнюю жизнь. Жизнь, в которой не было места страху.');
