INSERT INTO BOOK_CATALOG(ID_book,NAME,AUTHOR,GENRE,YEAR,COST,DESCRIPTION) VALUES  (33,'Ромео и Джульетта. Трагедии ','Шекспир Уильям ','трагедия','2017','574','Сборник произведений великого английского драматурга и поэта, крупнейшего гуманиста эпохи позднего Возрождения Уильяма Шекспира включает трагедии"Ромео и Джульетта", "Гамлет, принц Датский", "Отелло", "Король Лир", "Макбет", в которых в борьбе со злом утверждается величие духа человека.');
